#!/usr/bin/env python

import RPi.GPIO as GPIO
import sys
import redis
sys.path.append('/home/pi/Downloads/MFRC522-python')
from mfrc522 import SimpleMFRC522

r = redis.Redis(host='localhost', port=6379 )
reader = SimpleMFRC522()

print("Hold a tag near the reader")

try:
    id, text = reader.read()
    r.setex('ID',30 , id)
    r.set('Text', text)

finally:
    GPIO.cleanup()
